import React, { useState } from 'react';
import IncidenciaForm from './components/IncidenciaForm';
import IncidenciaList from './components/IncidenciaList';

function App() {
  const [showForm, setShowForm] = useState(false);  // Nuevo estado para mostrar/ocultar el formulario

  // Función para alternar la visibilidad del formulario
  const toggleForm = () => {
    setShowForm(!showForm);
  };

  return (
    <div className="App">
      <header>
        <h1>Gestión de Incidencias</h1>
        <button onClick={toggleForm}>
          {showForm ? 'Cancelar' : 'Agregar Nueva Incidencia'}
        </button>
      </header>

      {/* Muestra el formulario solo si showForm es true */}
      {showForm && <IncidenciaForm />}

      {/* Tabla de incidencias */}
      <IncidenciaList />
    </div>
  );
}

export default App;
