import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './IncidenciaList.css'; // Importar los estilos CSS

const IncidenciaList = () => {
  const [incidencias, setIncidencias] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchIncidencias = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/incidencias');
        setIncidencias(response.data);
      } catch (err) {
        setError('Hubo un error al obtener las incidencias. Intenta nuevamente.');
      } finally {
        setLoading(false);
      }
    };

    fetchIncidencias();
  }, []);

  return (
    <div className="incidencia-list-container">
      <h2>Lista de Incidencias</h2>
      {incidencias.length === 0 ? (
        <p>No hay incidencias registradas.</p>
      ) : (
        <table className="incidencia-table">
          <thead>
            <tr>
              <th>Empresa</th>
              <th>Incidencia</th>
              <th>Causa</th>
              <th>Afecta</th>
              <th>Reporte</th>
              <th>Atendida</th>
            </tr>
          </thead>
          <tbody>
            {incidencias.map((incidencia) => (
              <tr key={incidencia._id}>
                <td>{incidencia.empresa}</td>
                <td>{incidencia.incidencia}</td>
                <td>{incidencia.causa}</td>
                <td>{incidencia.afecta}</td>
                <td>{incidencia.reporte}</td>
                <td>{incidencia.atendida ? 'Sí' : 'No'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default IncidenciaList;
