import React, { useState, useEffect } from 'react';
import IncidenciaForm from './components/IncidenciaForm';
import IncidenciaList from './components/IncidenciaList';
import axios from 'axios';

function App() {
  const [incidencias, setIncidencias] = useState([]); // Estado global para almacenar incidencias
  const [showForm, setShowForm] = useState(false); // Controlar la visibilidad del formulario

  // Función para obtener las incidencias del backend
  const fetchIncidencias = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/incidencias');
      setIncidencias(response.data); // Actualizamos el estado con las incidencias
    } catch (error) {
      console.error('Error al obtener las incidencias:', error);
    }
  };

  // Llama a la función para cargar las incidencias al montar el componente
  useEffect(() => {
    fetchIncidencias();
  }, []);

  // Función para agregar una nueva incidencia
  const addIncidencia = (nuevaIncidencia) => {
    setIncidencias((prevIncidencias) => [nuevaIncidencia, ...prevIncidencias]); // Añade la nueva incidencia al inicio de la lista
  };

  return (
    <div className="App">
      <header>
        <h1>Gestión de Incidencias</h1>
        <button onClick={() => setShowForm(!showForm)}>
          {showForm ? 'Cancelar' : 'Agregar Nueva Incidencia'}
        </button>
      </header>

      {/* Formulario de incidencias */}
      {showForm && <IncidenciaForm addIncidencia={addIncidencia} />}

      {/* Lista de incidencias */}
      <IncidenciaList incidencias={incidencias} />
    </div>
  );
}

export default App;
